INSERT INTO `wp_comments` (`comment_ID`, `comment_post_ID`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_IP`, `comment_date`, `comment_date_gmt`, `comment_content`, `comment_karma`, `comment_approved`, `comment_agent`, `comment_type`, `comment_parent`, `user_id`) VALUES
(1, 1, 'Một người bình luận WordPress', 'wapuu@wordpress.example', 'https://wordpress.org/', '', '2018-04-03 08:17:54', '2018-04-03 08:17:54', 'Xin chào, đây là một bình luận\nĐể bắt đầu với quản trị bình luận, chỉnh sửa hoặc xóa bình luận, vui lòng truy cập vào khu vực Bình luận trong trang quản trị.\nAvatar của người bình luận sử dụng <a href=\"https://gravatar.com\">Gravatar</a>.', 0, '1', '', '', 0, 0),
(2, 44, 'WooCommerce', '', '', '', '2018-04-04 17:38:48', '2018-04-04 17:38:48', 'Thanh toán khi nhận hàng. Trạng thái đơn hàng đã được chuyển từ Chờ thanh toán sang Đang xử lý.', 0, '1', 'WooCommerce', 'order_note', 0, 0),
(3, 44, 'WooCommerce', '', '', '', '2018-04-04 17:38:49', '2018-04-04 17:38:49', 'Số lượng sản phẩm Vans Old Skool Checker Sidewall (VN0A38G1QMI) giảm từ 100 xuống 99.', 0, '1', 'WooCommerce', 'order_note', 0, 0),
(4, 110, 'WooCommerce', '', '', '', '2018-04-07 15:13:48', '2018-04-07 15:13:48', 'Chờ thanh toán chuyển khoản Trạng thái đơn hàng đã được chuyển từ Chờ thanh toán sang Tạm giữ.', 0, '1', 'WooCommerce', 'order_note', 0, 0),
(5, 110, 'WooCommerce', '', '', '', '2018-04-07 15:13:51', '2018-04-07 15:13:51', 'Số lượng sản phẩm Vans Old Skool Checker Sidewall (VN0A38G1QMI) giảm từ 99 xuống 97.', 0, '1', 'WooCommerce', 'order_note', 0, 0),
(6, 110, 'WooCommerce', '', '', '', '2018-04-07 15:13:54', '2018-04-07 15:13:54', 'Số lượng sản phẩm Chuck Taylor All Star Classic (121178) giảm từ 100 xuống 99.', 0, '1', 'WooCommerce', 'order_note', 0, 0),
(7, 111, 'WooCommerce', '', '', '', '2018-04-08 12:48:20', '2018-04-08 12:48:20', 'Đơn hàng chưa thanh toán đã bị hủy - giới hạn thời gian đã hết. Trạng thái đơn hàng đã được chuyển từ Chờ thanh toán sang Đã hủy.', 0, '1', 'WooCommerce', 'order_note', 0, 0),
(8, 112, 'WooCommerce', '', '', '', '2018-04-08 12:48:22', '2018-04-08 12:48:22', 'Đơn hàng chưa thanh toán đã bị hủy - giới hạn thời gian đã hết. Trạng thái đơn hàng đã được chuyển từ Chờ thanh toán sang Đã hủy.', 0, '1', 'WooCommerce', 'order_note', 0, 0);
